import React, { useState } from "react";
import {
  Box,
  Typography,
  Avatar,
  IconButton,
  Button,
  Container,
  Stack,
} from "@mui/material";
import ArrowBackIosNewIcon from "@mui/icons-material/ArrowBackIosNew";
import ArrowForwardIosIcon from "@mui/icons-material/ArrowForwardIos";

const testimonials = [
  {
    name: "Jannike Borg",
    role: "Publisher",
    text: `I quickly found the right tour for me, but I had a few questions about the hotel, I wrote to tech support and they answered all my questions within an hour. The vacation itself was perfect. Thank you very much. I will come back again and again.`,
    image: "data:image/jpeg;base64,",
  },
  {
    name: "LeBron Durant",
    role: "Flight attendant",
    text: `I quickly found the right tour for me, but I had a few questions about the hotel, I wrote to tech support and they answered all my questions within an hour. The vacation itself was perfect. Thank you very much. I will come back again and again.`,
    image: "data:image/jpeg;base64,",
  },
  {
    name: "Kaarel Piho",
    role: "Chiropodist",
    text: `I quickly found the right tour for me, but I had a few questions about the hotel, I wrote to tech support and they answered all my questions within an hour. The vacation itself was perfect. Thank you very much. I will come back again and again.`,
    image: "data:image/jpeg;base64,",
  },  {
    name: "Kaarel Piho",
    role: "Chiropodist",
    text: `I quickly found the right tour for me, but I had a few questions about the hotel, I wrote to tech support and they answered all my questions within an hour. The vacation itself was perfect. Thank you very much. I will come back again and again.`,
    image: "data:image/jpeg;base64,",
  },
];

const TestimonialsCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handlePrev = () => {
    setCurrentIndex((prev) =>
      prev === 0 ? testimonials.length - 1 : prev - 1
    );
  };

  const handleNext = () => {
    setCurrentIndex((prev) =>
      prev === testimonials.length - 1 ? 0 : prev + 1
    );
  };

  return (
    // <Box py={6} bgcolor="#fff">
            <Box sx={{ padding: 4, backgroundColor: "#f5f5f5", xs:"100%" ,   }}>
    
      <Container maxWidth="md">
        <Typography
          variant="h4"
          align="center"
          fontWeight="bold"
          gutterBottom
          mb={4}
        >
          Reviews
        </Typography>

        {/* Testimonials Wrapper */}
        <Box
          display="flex"
          alignItems="center"
          justifyContent="center"
          position="relative"
        >
          {/* Left Arrow */}
          <IconButton
            onClick={handlePrev}
            sx={{
              position: "relative",
              left: 0,

              backgroundColor: "grey.200",
              "&:hover": { backgroundColor: "grey.300" },
            }}
          >
            <ArrowBackIosNewIcon />
          </IconButton>

          {/* Testimonials */}
          <Box
            display="flex"
            justifyContent="center"
            alignItems="flex-start"
            gap={4}
            overflow="hidden"
            width="100%"
          >
            {testimonials.map((item, index) => {
              // Show current + next two testimonials
              const isVisible =
                index === currentIndex ||
                index === (currentIndex + 1) % testimonials.length ||
                index === (currentIndex + 2) % testimonials.length;
              return (
                isVisible && (
                  <Box
                    key={index}
                    width={{ xs: "100%", sm: "30%" }}
                    textAlign="center"
                  >
                    <Avatar
                      src={item.image}
                      alt={item.name}
                      sx={{ width: 80, height: 80, mb: 2, mx: "auto" }}
                    />
                    <Typography
                      variant="body1"
                      color="text.secondary"
                      mb={2}
                      sx={{ fontSize: "0.95rem" }}
                    >
                      {item.text}
                    </Typography>
                    <Typography variant="subtitle1" fontWeight="bold">
                      {item.name}
                    </Typography>
                    <Typography
                      variant="caption"
                      color="text.secondary"
                      fontStyle="italic"
                    >
                      {item.role}
                    </Typography>
                  </Box>
                )
              );
            })}
          </Box>

          {/* Right Arrow */}
          <IconButton
            onClick={handleNext}
            sx={{
              position: "relative",
              right: 0,
              backgroundColor: "grey.200",
              "&:hover": { backgroundColor: "grey.300" },
            }}
          >
            <ArrowForwardIosIcon />
          </IconButton>
        </Box>

        {/* More Reviews Button */}
        <Box mt={4} display="flex" justifyContent="center">
          <Button
            variant="contained"
            size="small"
            sx={{
              borderRadius: "25px",
              backgroundColor: "#ef6c57",
              px: 4,
              py: 1,
              fontWeight: "bold",
              "&:hover": {
                backgroundColor: "#d85c47",
              },
            }}
          >
            More reviews
          </Button>
        </Box>
      </Container>
    </Box>
  );
};

export default TestimonialsCarousel;
